function toggleMobileNavState(){const e=document.querySelector("body");e.classList.toggle("nav--active")}function initBurger(){const e=document.querySelector(".burger");e.addEventListener("click",toggleMobileNavState)}initBurger();var normal_title=document.title;document.addEventListener("visibilitychange",function(){document.visibilityState=="hidden"?document.title="不要打喷嚏🤧":document.title=normal_title})